mapper.select:
select uid, tel, password, dept, name, status
from tb_user
where uid = #{uid,jdbcType=INTEGER}

mapper.update:
update tb_user
set password = #{password,jdbcType=VARCHAR}
where tel = #{tel,jdbcType=VARCHAR}

mapper.select:
SELECT count(1) from (
select

from tb_user


    and name like '%${name}%'


    and tel like '%${userPhone}%'


    and dept like '%${dept}%'


    and status = #{status}


limit #{start},#{limit}
) t
